    ["@media (max-width: 2000px)"]: {
      marginTop: "5rem",
      fontSize: "7rem",
    },
    ["@media (max-width: 1000px)"]: {
      fontSize: "5.5rem",
    },
    ["@media (max-width: 800px)"]: {
      fontSize: "4.5rem",
    },
    ["@media (max-width: 600px)"]: {
      fontSize: "3.5rem",
    },
    ["@media (max-width: 400px)"]: {
