      fontSize: "2rem",
    },
    ["@media screen and (orientation: landscape) and (max-device-width: 400px)"]: {
      fontSize: "1.5rem",
    },
  },
  coverSubTitle: {
    color: "#FFFFFF",
    fontFamily:
      "Poppins, -apple-system, Helvetica Neue, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, sans-serif",
    fontWeight: "300",
    fontSize: "1.3rem",
    ["@media (max-width: 4000px)"]: {
      fontSize: "3.5rem",
    },
    ["@media (max-width: 3500px)"]: {
      fontSize: "3rem",
