import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Link from "next/link";

const useStyles = makeStyles((theme) => ({

  logo:{
    margin: '0 auto',
    padding: '10px',
    fontFamily: 'FSDiavloBold',
    fontSize: "40px",
    color: "#fddfa2",

    height: "60px",
    backgroundColor: '#012b1e',
    link:{
        display: "flex",
        alignItems: "center"
      },
  },
  slogan: {
    fontFamily: 'FSDiavloMedium',
    color: "#fad281",
    fontSize: "16px",
    lineHeight: "18px",
    textAlign: "center",
  }

}));

function Header() {
  const classes = useStyles();
  useEffect(() => {
    setTouchDevice("ontouchstart" in document.documentElement);
  }, []);
  const [touchDevice, setTouchDevice] = useState(true);
  return (
    <>
      {(
        <>
          <div >
            <Container>
              <Grid
                container
                direction="column"
                justify="space-between"
                spacing={10}
                alignItems="flex-start"
              >
                <Grid item xs={12}>
                    <div className={classes.header}>
						<a
                        className={classes.link}

						onClick={() => {
							window.location.href = '/';
						}}
					>
							<div className={classes.logo}>
								Old
								<br />
								Henry
							</div>
							<div className={classes.slogan}>{"Mì Kiều Mạch"}</div>
							</a>

							<img src="../messenger.png" className={classes.messenger2} ></img>
						</div>

                </Grid>

              </Grid>
            </Container>
          </div>
        </>
      )}
    </>
  );
}

export default Header;
